package seed_sdet_course_core_java;

public class Day_1_Max_of_Two_Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=10;
		int max=0;
		if(a>b)
		{
			max=a;
			System.out.println("The maximum number is:"+max);
		}
		else {
			max=b;
			System.out.println("The maximum number is:"+max);
		}

	}

}
